mapper.select:



        AND name like concat('%', #{name}, '%')


        AND visible = #{visible}


        AND status = #{status}


order by parent_id, order_num

select distinct m.id, m.parent_id, m.name, m.path, m.component, m.visible, m.status, ifnull(m.perms,'') as perms, m.is_frame, m.menu_type, m.icon, m.order_num, m.create_time from pbms_menu m left join pbms_role_menu rm on m.id = rm.menu_id left join pbms_user_role ur on rm.role_id = ur.role_id left join pbms_role ro on ur.role_id = ro.id where ur.user_id = #{params.userId}
AND name like concat('%', #{name}, '%')
AND visible = #{visible}
AND status = #{status}
order by m.parent_id, m.order_num
